// src/components/Home.js
import React from 'react';

const technicalSkills = [
  { name: 'HTML5', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/html5/html5-original.svg' },
  { name: 'CSS3', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/css3/css3-original.svg' },
  { name: 'JavaScript', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/javascript/javascript-original.svg' },
  { name: 'Python', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg' },
  { name: 'Java', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/java/java-original.svg' },
  { name: 'jQuery', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/jquery/jquery-original.svg' },
  { name: 'ASP.NET', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/dot-net/dot-net-original.svg' },
  { name: 'SQL', img: 'https://cdn-icons-png.flaticon.com/512/2772/2772128.png' },
  { name: 'MySQL', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mysql/mysql-original.svg' },
  { name: 'Oracle', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/oracle/oracle-original.svg' },
  { name: 'WordPress', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/wordpress/wordpress-original.svg' },
  { name: 'Shopify', img: 'https://cdn-icons-png.flaticon.com/512/5968/5968906.png' },
  { name: 'AWS', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/amazonwebservices/amazonwebservices-original-wordmark.svg' },
  { name: 'Azure', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/azure/azure-original.svg' },
  { name: 'GCP', img: '/logo/googlecloud.png' },
  { name: 'Office 365', img: '/logo/office365.jpg' },
  { name: 'Google Charts', img: '/logo/googlecharts.png' },
  { name: 'Draw.io', img: '/logo/drawio.png' }
];

const professionalDevelopment = [
  {
    name: 'Professional Year (Oct 2022 – Apr 2023), Performance Education',
    img: '/logo/pe.png',
    alt: 'Performance Education',
  },
  {
    name: 'AWS Cloud Practitioner Essentials, AWS Training',
    img: '/logo/aws.png',
    alt: 'AWS',
  },
  {
    name: 'Google Professional Cloud Developer Certificate',
    img: '/logo/gcpd.png',
    alt: 'Google Cloud',
  },
  {
    name: 'Business Analysis and Process Management, Coursera',
    img: '/logo/c.png',
    alt: 'Coursera',
  },
  {
    name: 'Marketing Tools: Social Media, LinkedIn Learning',
    img: '/logo/linkedin.png',
    alt: 'LinkedIn Learning',
  },
  {
    name: 'Software Development Analytics Certification, SAS',
    img: '/logo/sas.png',
    alt: 'SAS',
  },
  {
    name: 'Web Page Development Certification, Alison',
    img: '/logo/alison.jpg',
    alt: 'Alison',
  },
  {
    name: 'Database Foundations, LinkedIn Learning',
    img: '/logo/linkedin.png',
    alt: 'LinkedIn Learning',
  },
  {
    name: 'Salesforce Developer AI Introduction, Alison',
    img: '/logo/alison.jpg',
    alt: 'Alison',
  },
  {
    name: 'Diploma in HTML5, CSS3, JavaScript and Python, Alison',
    img: '/logo/alison.jpg',
    alt: 'Alison',
  }
];

const sectionStyle = {
  backgroundColor: '#111',
  color: '#eee',
  padding: '2rem',
  marginBottom: '2rem',
  borderRadius: '12px',
  maxWidth: '1200px',
  marginLeft: 'auto',
  marginRight: 'auto',
};

const Home = () => {
  return (
    <>
      <style>{`
        /* Animations */
        @keyframes fadeSlideIn {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .fade-slide-in {
          animation: fadeSlideIn 0.8s forwards;
          opacity: 0;
        }

        /* Logo styling */
        .logo-img {
          height: 80px;
          border-radius: 10px;
          object-fit: contain;
          box-shadow: 0 4px 10px rgba(255, 255, 255, 0.2);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
          cursor: pointer;
        }
        .logo-img:hover {
          transform: scale(1.1);
          box-shadow: 0 6px 14px rgba(255, 255, 255, 0.35);
        }

        /* Responsive grid for skills and development */
        .grid-container {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
          gap: 1.5rem;
          align-items: center;
        }

        .skill-item, .dev-item {
          background: #222;
          padding: 1rem;
          border-radius: 12px;
          text-align: center;
          color: #eee;
          box-shadow: 0 3px 8px rgba(255, 255, 255, 0.1);
          transition: background-color 0.3s ease;
        }
        .skill-item:hover, .dev-item:hover {
          background-color: #333;
        }
        .skill-name, .dev-name {
          margin-top: 0.8rem;
          font-weight: 600;
          font-size: 1rem;
          user-select: none;
        }

        /* Hero section */
        #hero {
          background: linear-gradient(135deg, #6b73ff 0%, #000dff 100%);
          color: white;
          padding: 4rem 2rem;
          text-align: center;
          border-radius: 14px;
          box-shadow: 0 10px 30px rgba(0, 13, 255, 0.5);
          margin-bottom: 3rem;
          max-width: 900px;
          margin-left: auto;
          margin-right: auto;
          animation: fadeSlideIn 1s forwards;
        }
        #hero h2 {
          font-size: 3rem;
          margin-bottom: 1rem;
          text-shadow: 0 0 8px rgba(255, 255, 255, 0.9);
        }
        #hero p {
          font-size: 1.3rem;
          font-weight: 500;
          letter-spacing: 0.05em;
          text-shadow: 0 0 6px rgba(255, 255, 255, 0.6);
          margin-top: 0;
        }

        /* Responsive adjustments */
        @media (max-width: 600px) {
          #hero h2 {
            font-size: 2.2rem;
          }
          #hero p {
            font-size: 1rem;
          }
          .logo-img {
            height: 60px;
          }
          .skill-name, .dev-name {
            font-size: 0.9rem;
          }
        }
      `}</style>

      {/* Hero Section */}
      <section
        id="hero"
        style={{
          background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
          color: '#fff',
          padding: '6rem 2rem',
          textAlign: 'center',
          borderRadius: 8,
          marginBottom: '2rem',
          overflow: 'hidden',
          position: 'relative',
        }}
      >
        <h1
          style={{
            fontSize: '3.5rem',
