import React from 'react';
import { FaLaptopCode } from 'react-icons/fa';
import heroImage from '../assets/hero-image.jpg'; // Put your hero image in src/assets folder

const Hero = () => {
  return (
    <section
      id="hero"
      className="d-flex flex-column justify-content-center align-items-center"
      style={{
        height: '100vh',
        backgroundColor: '#000',
        color: '#c9d1d9',
        textAlign: 'center',
        paddingTop: '70px',
        position: 'relative',
      }}
    >
      <FaLaptopCode size={80} color="#61dafb" className="mb-3" />
      <h1 className="display-3 fw-bold mb-3">Swarnim Abiral Koirala</h1>
      <p className="lead mb-4" style={{ maxWidth: '600px' }}>
        Graduate in Bachelor of Information Technology | Software Developer | Cloud Enthusiast | Problem Solver
      </p>
      <a
        href="#career-objective"
        className="btn btn-outline-primary btn-lg"
        style={{ color: '#c9d1d9', borderColor: '#c9d1d9' }}
      >
        View My Work
      </a>
      {/* Optional side hero image */}
      <img
        src={heroImage}
        alt="Coding illustration"
        style={{
          position: 'absolute',
          bottom: '20px',
          right: '20px',
          width: '200px',
          opacity: 0.15,
          pointerEvents: 'none',
        }}
      />
    </section>
  );
};

export default Hero;
