// src/components/Home.js
import React from 'react';

const technicalSkills = [
  { name: 'HTML5', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/html5/html5-original.svg' },
  { name: 'CSS3', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/css3/css3-original.svg' },
  { name: 'JavaScript', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/javascript/javascript-original.svg' },
  { name: 'Python', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/python/python-original.svg' },
  { name: 'Java', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/java/java-original.svg' },
  { name: 'jQuery', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/jquery/jquery-original.svg' },
  { name: 'ASP.NET', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/dot-net/dot-net-original.svg' },
  { name: 'SQL', img: 'https://cdn-icons-png.flaticon.com/512/2772/2772128.png' },
  { name: 'MySQL', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/mysql/mysql-original.svg' },
  { name: 'Oracle', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/oracle/oracle-original.svg' },
  { name: 'WordPress', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/wordpress/wordpress-original.svg' },
  { name: 'Shopify', img: 'https://cdn-icons-png.flaticon.com/512/5968/5968906.png' },
  { name: 'AWS', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/amazonwebservices/amazonwebservices-original-wordmark.svg' },
  { name: 'Azure', img: 'https://cdn.jsdelivr.net/gh/devicons/devicon/icons/azure/azure-original.svg' },
  { name: 'GCP', img: '/logo/googlecloud.png' },
  { name: 'Office 365', img: '/logo/office365.jpg' },
  { name: 'Google Charts', img: '/logo/googlecharts.png' },
  { name: 'Draw.io', img: '/logo/drawio.png' }
];

const certifications = [
  {
    name: 'Responsive Web Design',
    org: 'freeCodeCamp',
    year: '2023',
    logo: 'https://www.freecodecamp.org/news/content/images/2022/08/freecodecamp.png',
  },
  {
    name: 'SQL Fundamentals',
    org: 'SoloLearn',
    year: '2023',
    logo: 'https://www.sololearn.com/Images/logo.png',
  },
  {
    name: 'AWS Cloud Practitioner',
    org: 'Amazon Web Services',
    year: '2024',
    logo: 'https://a0.awsstatic.com/libra-css/images/logos/aws_logo_smile_1200x630.png',
  },
];

const Home = () => {
  const sectionStyle = {
    backgroundColor: '#111',
    color: '#eee',
    padding: '2rem',
    marginBottom: '2rem',
    borderRadius: '8px',
  };

  const logoStyle = {
    width: 70,
    height: 70,
    objectFit: 'contain',
    marginBottom: '0.5rem',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(255, 255, 255, 0.15)',
  };

  const logoLargeStyle = {
    width: 90,
    height: 90,
    objectFit: 'contain',
    borderRadius: '12px',
    boxShadow: '0 2px 6px rgba(255, 255, 255, 0.15)',
    cursor: 'pointer',
    transition: 'transform 0.3s ease',
  };

  return (
    <div>
      {/* Hero Section */}
      <section
        id="hero"
        style={{
          background: 'linear-gradient(135deg, #0f2027, #203a43, #2c5364)',
          color: '#fff',
          padding: '6rem 2rem',
          textAlign: 'center',
          borderRadius: 8,
          marginBottom: '2rem',
          overflow: 'hidden',
          position: 'relative',
        }}
      >
        <h1
          style={{
            fontSize: '3.5rem',
            fontWeight: '900',
            marginBottom: '1rem',
            opacity: 0,
            transform: 'translateY(30px)',
            animation: 'fadeSlideIn 1s forwards',
            animationTimingFunction: 'cubic-bezier(0.68, -0.55, 0.27, 1.55)',
          }}
        >
          Welcome to my portfolio!!!
        </h1>

        <p
          style={{
            fontSize: '1.5rem',
            marginBottom: '2rem',
            fontWeight: '500',
            minHeight: '2rem',
            color: '#ddd',
            fontFamily: 'Courier, monospace',
            whiteSpace: 'nowrap',
            overflow: 'hidden',
            borderRight: '3px solid #fff',
            width: 'fit-content',
            marginLeft: 'auto',
            marginRight: 'auto',
            animation: 'typing 3s steps(20) 1 forwards, blink 0.75s step-end infinite',
            opacity: 0,
            animationDelay: '1.2s',
            animationFillMode: 'forwards',
          }}
        >
          Welcome to My Portfolio!
        </p>

        <button
          style={{
            padding: '1rem 2.5rem',
            fontSize: '1.1rem',
            fontWeight: '600',
            color: '#203a43',
            backgroundColor: '#fff',
            border: 'none',
            borderRadius: '30px',
            cursor: 'pointer',
            boxShadow: '0 5px 15px rgba(255,255,255,0.3)',
            transition: 'background-color 0.3s ease, transform 0.3s ease',
            opacity: 0,
            transform: 'translateY(30px)',
            animation: 'fadeSlideIn 1s forwards',
            animationDelay: '2.6s',
            animationTimingFunction: 'cubic-bezier(0.68, -0.55, 0.27, 1.55)',
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = '#e6e6e6';
            e.currentTarget.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = '#fff';
            e.currentTarget.style.transform = 'scale(1)';
          }}
          onClick={() => window.scrollTo({ top: window.innerHeight, behavior: 'smooth' })}
        >
          Explore
        </button>

        <style>{`
          @keyframes fadeSlideIn {
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }
          @keyframes typing {
            from { width: 0 }
            to { width: 18ch }
          }
          @keyframes blink {
            50% { border-color: transparent }
          }
          @media (max-width: 768px) {
            #hero h1 {
              font-size: 2.8rem !important;
            }
            #hero p {
              font-size: 1.2rem !important;
            }
            #hero button {
              font-size: 1rem !important;
              padding: 0.8rem 2rem !important;
            }
          }
          @media (max-width: 480px) {
            #hero h1 {
              font-size: 2.4rem !important;
            }
            #hero p {
              font-size: 1rem !important;
            }
            #hero button {
              font-size: 0.9rem !important;
              padding: 0.7rem 1.8rem !important;
            }
          }
        `}</style>
      </section>

      {/* Career Objective */}
      <section id="career-objective" style={sectionStyle}>
        <h2>Career Objective</h2>
        <p>
          Graduate in Bachelor of Information Technology from Melbourne Polytechnic with
          industry-based project experience. Experienced in Python, WordPress, HTML5, CSS,
          and JavaScript. Previously an IT Software Developer intern at 10th Dan, Melbourne.
          Currently a Multi-Site Store Manager with 5 years customer service experience.
          Seeking to develop an IT career and skillset.
        </p>
      </section>

      {/* Technical Skills */}
      <section id="technical-skills" style={sectionStyle}>
        <h2>Technical Skills</h2>
        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '1.5rem',
            justifyContent: 'center',
            alignItems: 'center',
            marginBottom: '1rem',
          }}
        >
          {technicalSkills.map((skill, index) => (
            <div
              key={skill.name}
              style={{
                textAlign: 'center',
                width: 100,
                color: '#eee',
                cursor: 'default',
                transition: 'transform 0.3s ease',
                animation: 'fadeSlideIn 0.6s forwards',
                animationDelay: `calc(0.1s * ${index})`,
                opacity: 0,
              }}
              title={skill.name}
              onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.1)'}
              onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
            >
              <img
                src={skill.img}
                alt={skill.name}
                style={logoStyle}
              />
              <div style={{ fontSize: '1rem', fontWeight: '600' }}>{skill.name}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Education */}
      <section id="education" style={sectionStyle}>
        <h2>Education</h2>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '1rem',
            flexWrap: 'wrap',
          }}
        >
          <img
            src="/logo/melbourne-polytechnic.png"
            alt="Melbourne Polytechnic"
            style={logoLargeStyle}
            title="Melbourne Polytechnic"
            onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.1)')}
            onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          />
          <div>
            <h3>Bachelor of Information Technology</h3>
            <p>Major in Software Development</p>
            <p>Melbourne Polytechnic (2020 - 2023)</p>
          </div>
        </div>
      </section>

      {/* Professional Development */}
      <section id="professional-development" style={sectionStyle}>
        <h2>Professional Development & Certifications</h2>

        {/* Internship */}
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '1rem',
            flexWrap: 'wrap',
            marginBottom: '1.5rem',
          }}
        >
          <img
            src="/logo/10th-dan.png"
            alt="10th Dan"
            style={logoLargeStyle}
            title="10th Dan"
            onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.1)')}
            onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          />
          <div>
            <h3>IT Software Developer Intern</h3>
            <p>10th Dan, Melbourne (Jan 2023 - Jun 2023)</p>
            <p>Developed custom WordPress plugins using Python.</p>
          </div>
        </div>

        {/* Certifications */}
        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            gap: '1.5rem',
            justifyContent: 'center',
          }}
        >
          {certifications.map((cert, index) => (
            <div
              key={cert.name}
              style={{
                width: 180,
                backgroundColor: '#222',
                borderRadius: 12,
                padding: '1rem',
                textAlign: 'center',
                boxShadow: '0 2px 8px rgba(255, 255, 255, 0.1)',
                cursor: 'default',
                transition: 'transform 0.3s ease',
                animation: 'fadeSlideIn 0.6s forwards',
                animationDelay: `calc(0.15s * ${index})`,
                opacity: 0,
              }}
              title={`${cert.name} - ${cert.org} (${cert.year})`}
              onMouseEnter={e => e.currentTarget.style.transform = 'scale(1.05)'}
              onMouseLeave={e => e.currentTarget.style.transform = 'scale(1)'}
            >
              <img
                src={cert.logo}
                alt={cert.org}
                style={{ width: 60, height: 60, objectFit: 'contain', marginBottom: '0.5rem' }}
              />
              <div style={{ fontWeight: '700', fontSize: '1rem' }}>{cert.name}</div>
              <div style={{ fontSize: '0.85rem', color: '#ccc' }}>
                {cert.org} ({cert.year})
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Work Experience */}
      <section id="work-experience" style={sectionStyle}>
        <h2>Work Experience</h2>
        <div
          style={{
            display: 'flex',
            alignItems: 'center',
            gap: '1rem',
            flexWrap: 'wrap',
          }}
        >
          <img
            src="/logo/fossil.png"
            alt="Fossil"
            style={logoLargeStyle}
            title="Fossil"
            onMouseEnter={(e) => (e.currentTarget.style.transform = 'scale(1.1)')}
            onMouseLeave={(e) => (e.currentTarget.style.transform = 'scale(1)')}
          />
          <div>
            <h3>Multi-Site Store Manager</h3>
            <p>Fossil, Melbourne (2018 - Present)</p>
            <ul>
              <li>Managed multiple retail stores with teams of up to 15 people.</li>
              <li>Achieved sales targets and improved customer satisfaction.</li>
              <li>Implemented staff training and operational improvements.</li>
            </ul>
          </div>
        </div>
      </section>

      {/* References */}
      <section id="references" style={sectionStyle}>
        <h2>References</h2>
        <p>Available upon request.</p>
      </section>

      {/* Global Styles for Animations */}
      <style>{`
        @keyframes fadeSlideIn {
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
      `}</style>
    </div>
  );
};

export default Home;
