import React from 'react';

const Hero = () => {
  return (
    <section
      id="hero"
      style={{
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background:
          'linear-gradient(135deg, #0d1117 0%, #161b22 100%)',
        color: '#58a6ff',
        padding: '2rem',
        textAlign: 'center',
        flexDirection: 'column',
        gap: '1.5rem',
      }}
    >
      <img
        src="https://media.licdn.com/dms/image/v2/C5603AQHKO6yXMLbLrw/profile-displayphoto-shrink_800_800/profile-displayphoto-shrink_800_800/0/1652352670525?e=1753315200&v=beta&t=NIOGSVk-MglIg15-2PeebVh2r8nqlNSBFTVHWi0ypwE" // Replace with your photo URL
        alt="Your Name"
        style={{
          borderRadius: '50%',
          width: '180px',
          height: '180px',
          boxShadow: '0 0 25px #58a6ff',
          objectFit: 'cover',
        }}
      />
      <h1 style={{ fontSize: '3.5rem', margin: 0, fontWeight: '700' }}>
        Swarnim Abiral Koirala
      </h1>
      <p
        style={{
          fontSize: '1.5rem',
          maxWidth: '600px',
          color: '#adbac7',
          margin: '0 auto',
          fontWeight: '400',
        }}
      >
        Bachelor of Information Technology graduate passionate about software development, cloud computing, and building modern web solutions.
      </p>
      <a
        href="#careerobjective"
        style={{
          padding: '0.8rem 2rem',
          fontSize: '1.2rem',
          fontWeight: '600',
          color: '#0d1117',
          backgroundColor: '#58a6ff',
          borderRadius: '30px',
          textDecoration: 'none',
          boxShadow: '0 0 15px #58a6ff',
          transition: 'background-color 0.3s ease, color 0.3s ease',
        }}
        onMouseEnter={e => {
          e.target.style.backgroundColor = '#1f6feb';
          e.target.style.color = '#c9d1d9';
        }}
        onMouseLeave={e => {
          e.target.style.backgroundColor = '#58a6ff';
          e.target.style.color = '#0d1117';
        }}
      >
        Explore My Work
      </a>
    </section>
  );
};

export default Hero;
