import React, { useState } from 'react';
import NavbarComponent from './components/NavbarComponent';
import Hero from './components/Hero';
import Home from './components/Home';
import SectionModal from './components/SectionModal';
import logoImage from './assets/logo.png';
import './App.css';


function App() {
  const [modalSection, setModalSection] = useState(null);

  const App = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Wait for 3 seconds, then hide the loading screen
    const timer = setTimeout(() => setLoading(false), 3000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      {loading ? (
        <div className="loading-screen">
          <img src={logoImage} alt="Loading" className="fade-image" />
        </div>
      ) : (
        <Home />
      )}
    </div>
  );
};


  const handleNavClick = (sectionId) => {
    // Scroll to the section smoothly on navbar click
    const element = document.getElementById(sectionId);
    if (element) {
      const yOffset = -70; // height of navbar for offset
      const y = element.getBoundingClientRect().top + window.pageYOffset + yOffset;
      window.scrollTo({ top: y, behavior: 'smooth' });
    }
  };

  const handleSectionClick = (sectionId) => {
    // Open modal when clicking inside section
    setModalSection(sectionId);
  };

  const closeModal = () => setModalSection(null);

  return (
    <>
      <NavbarComponent onNavClick={handleNavClick} />
      <Hero />
      <div className="container my-5">
        <Home onSectionClick={handleSectionClick} />
      </div>
      <SectionModal sectionId={modalSection} onClose={closeModal} />
    </>
  );
}

export default App;
